import React, { useState } from "react";
import "./App.css";

const categories = {
  "మసాలా (Masala)": [
    { id: 1, name: "జీలకర (Cumin Seeds)", price: 100, image: "https://res.cloudinary.com/dj0u6aez5/image/upload/v1739518682/IMG-20250214-WA0018_q9162d.jpg" },
    { id: 2, name: "ఆవాలు (Mustard Seeds)", price: 100, image: "https://res.cloudinary.com/dj0u6aez5/image/upload/v1739518683/IMG-20250214-WA0022_cg6es8.jpg" },
  ],
};

const App = () => {
  const [cart, setCart] = useState([]);
  const [adminCode, setAdminCode] = useState("");
  const [isAdmin, setIsAdmin] = useState(false);
  const [products, setProducts] = useState(categories["మసాలా (Masala)"]);
  const [editProduct, setEditProduct] = useState(null);

  const checkAdminCode = () => {
    if (adminCode === "123456") {
      setIsAdmin(true);
    }
  };

  const handleEdit = (product) => {
    setEditProduct(product);
  };

  const handleSave = () => {
    setProducts(products.map((p) => (p.id === editProduct.id ? editProduct : p)));
    setEditProduct(null);
  };

  const handleDelete = (id) => {
    setProducts(products.filter((p) => p.id !== id));
  };

  const handleAddProduct = () => {
    const newProduct = {
      id: products.length + 1,
      name: "New Product",
      price: 0,
      image: "",
    };
    setProducts([...products, newProduct]);
  };

  return (
    <div>
      <div className="admin-panel">
        <input type="text" value={adminCode} onChange={(e) => setAdminCode(e.target.value)} placeholder="Enter Admin Code" />
        <button onClick={checkAdminCode}>Enter</button>
      </div>

      {isAdmin && (
        <div>
          <h2>Admin Panel</h2>
          <button onClick={handleAddProduct}>Add New Product</button>
          {products.map((product) => (
            <div key={product.id}>
              <input type="text" value={product.name} onChange={(e) => setEditProduct({ ...product, name: e.target.value })} />
              <input type="text" value={product.image} onChange={(e) => setEditProduct({ ...product, image: e.target.value })} />
              <input type="number" value={product.price} onChange={(e) => setEditProduct({ ...product, price: Number(e.target.value) })} />
              <button onClick={handleSave}>Save</button>
              <button onClick={() => handleDelete(product.id)}>Delete</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
